CREATE TABLE qu_user (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,username varchar(255) NOT NULL,password varchar(255) NOT NULL,tel varchar(11) NOT NULL,group_id int(11) NOT NULL,project_id int(11) NOT NULL,remark varchar(255) DEFAULT NULL,code varchar(10));
CREATE TABLE qu_user_group (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,group_name varchar(255) NOT NULL);
CREATE TABLE qu_project (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,project_name varchar(255) NOT NULL,url varchar(255) NOT NULL,description varchar(255),lasttime varchar(255) NOT NULL,local_url varchar(255));
CREATE TABLE qu_area (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,area_name varchar(255) NOT NULL,project_id int(11) NOT NULL,description varchar(255),lasttime varchar(255) NOT NULL);
CREATE TABLE qu_classify (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,classify_name varchar(255) NOT NULL,project_id int(11) NOT NULL,area_id int(11) NOT NULL,description varchar(255),lasttime varchar(255) NOT NULL);
CREATE TABLE qu_task (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,task_name varchar(255) NOT NULL,project_id int(11) NOT NULL,area_id int(11) NOT NULL,classify_id int(11) NOT NULL,description varchar(255),url varchar(255) NOT NULL,lasttime varchar(255) NOT NULL,local_url varchar(255));
CREATE TABLE qu_acceptance_standard (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,project_id int(11) NOT NULL,area_id int(11) NOT NULL,classify_id int(11) NOT NULL,task_id int(11) NOT NULL,accept_name varchar(255) NOT NULL,standard varchar(255) NOT NULL, url varchar(255) NOT NULL,lasttime varchar(255) NOT NULL,local_url varchar(255));
CREATE TABLE qu_record (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,project_id int(11) NOT NULL,area_id int(11) NOT NULL,classify_id int(11) NOT NULL,task_id int(11) NOT NULL,create_time varchar(255) NOT NULL,submit int(1) NOT NULL DEFAULT 0,user_id int(11) NOT NULL, store_path varchar(255) NOT NULL,backend_id int(11) DEFAULT 0);
CREATE TABLE qu_actual_situation (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE NOT NULL,record_id int(11) NOT NULL,url varchar(255),description varchar(255) NOT NULL,score float NOT NULL,submit int(1) NOT NULL DEFAULT 0,backend_id int(11) DEFAULT 0,create_time varchar(255),local_url varchar(255),accept_id int(11) NOT NULL);